import React, { useState } from 'react';
import '../App.css';
import { Button } from './Button';
import './HeroSection.css';
import SignIn from './pages/SignIn';
import Announcements from './Announcements'; // import the Announcements component
import { useHistory } from 'react-router-dom';

function HeroSection() {
  const [showSignIn, setShowSignIn] = useState(false);
  const [showAnnouncements, setShowAnnouncements] = useState(false); // add state for displaying announcements
  const history = useHistory();

  const handleSignInClick = () => {
    setShowSignIn(true);
    history.push('/login');
  };

  const handleSignInClose = () => {
    setShowSignIn(false);
  };

  const handleAnnouncementsClick = () => {
    setShowAnnouncements(!showAnnouncements);
  };

  return (
    <div className='hero-container'>
      <video src='/videos/video-3.mp4' autoPlay loop muted />
      <h1>Best Food Friends</h1>
      <p>Best Food Donation App</p>
      <div className='hero-btns'>
        <Button
          className='btns'
          buttonStyle='btn--outline'
          buttonSize='btn--large'
          onClick={handleSignInClick}
        >
          SIGN IN
        </Button>
        <Button
          className='btns'
          buttonStyle='btn--outline'
          buttonSize='btn--large'
          onClick={handleAnnouncementsClick} // add onClick handler for displaying announcements
        >
          ANNOUNCEMENTS
        </Button>
        <Button
          className='btns'
          buttonStyle='btn--primary'
          buttonSize='btn--large'
        >
          GET STARTED <i className='far fa-play-circle' />
        </Button>
      </div>
      {showSignIn && <SignIn onClose={handleSignInClose} />}
      {showAnnouncements && <Announcements />} // display the Announcements component if showAnnouncements is true
    </div>
  );
}

export default HeroSection;
